import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import API from '../utils/api';

const AllRounds = () => {
  const { id } = useParams(); // tournament ID
  const [tournament, setTournament] = useState(null);

  useEffect(() => {
    const fetchTournament = async () => {
      try {
        const res = await API.get(`/tournaments/${id}`);
        setTournament(res.data);
      } catch (err) {
        console.error('Failed to fetch tournament:', err);
      }
    };

    fetchTournament();
  }, [id]);

  if (!tournament) return <div className="container mt-4">Loading tournament...</div>;

  return (
    <div className="container mt-4">
      <h2>{tournament.name} - All Rounds</h2>
      {tournament.rounds.length === 0 ? (
        <p>No rounds yet.</p>
      ) : (
        tournament.rounds.map((round) => (
          <div className="card mt-3" key={round._id || round.number}>
            <div className="card-body">
              <h5 className="card-title">Round {round.number}</h5>
              <ul className="list-group">
                {round.pairings.map((pair, index) => (
                  <li className="list-group-item" key={index}>
                    {pair.p1?.name || 'BYE'} vs {pair.p2?.name || 'BYE'}
                  </li>
                ))}
              </ul>
            </div>
          </div>
        ))
      )}
    </div>
  );
};

export default AllRounds;
