import React, { useEffect, useState, useCallback } from 'react';
import { useParams } from 'react-router-dom';
import API from '../utils/api';

const ViewTournament = () => {
  const { id } = useParams();
  const [tournament, setTournament] = useState(null);
  const [playerName, setPlayerName] = useState('');

  const fetchTournament = useCallback(async () => {
    try {
      const res = await API.get(`/tournaments/${id}`);
      setTournament(res.data);
    } catch (err) {
      console.error('❌ Error fetching tournament:', err);
    }
  }, [id]);

  useEffect(() => {
    fetchTournament();
  }, [fetchTournament]);

  const addPlayer = async () => {
    if (!playerName.trim()) return;
    try {
      await API.post(`/tournaments/${id}/players`, { name: playerName });
      setPlayerName('');
      fetchTournament();
    } catch (err) {
      console.error('❌ Error adding player:', err);
    }
  };

  const generateRound = async () => {
    try {
      await API.post(`/swiss/${id}/next-round`);
      fetchTournament();
    } catch (err) {
      console.error('❌ Error generating round:', err);
    }
  };

  if (!tournament) return <div className="container mt-4">Loading tournament...</div>;

  return (
    <div className="container mt-4">
      <h2>{tournament.name}</h2>

      <div className="mt-3">
        <input
          type="text"
          className="form-control"
          placeholder="Player Name"
          value={playerName}
          onChange={(e) => setPlayerName(e.target.value)}
        />
        <button className="btn btn-success mt-2" onClick={addPlayer}>Add Player</button>
        <button className="btn btn-warning mt-2 ms-2" onClick={generateRound}>Generate Round</button>
      </div>

      <h4 className="mt-4">Players</h4>
      <ul className="list-group">
        {tournament.players.map((p) => (
          <li key={p._id} className="list-group-item">
            {p.name} - {p.score} pts
          </li>
        ))}
      </ul>

      <h4 className="mt-4">Rounds</h4>
      {tournament.rounds.map((round) => (
        <div key={round._id} className="card mt-3">
          <div className="card-body">
            <h5 className="card-title">Round {round.number}</h5>
            <ul className="list-group">
              {round.pairings.map((pair, idx) => (
                <li key={idx} className="list-group-item">
                  {pair.p1?.name || 'BYE'} vs {pair.p2?.name || 'BYE'}
                </li>
              ))}
            </ul>
          </div>
        </div>
      ))}
    </div>
  );
};

export default ViewTournament;
