import React, { useEffect, useState, useCallback } from 'react';
import { useParams } from 'react-router-dom';
import API from '../utils/api';

const ViewTournament = () => {
  const { id } = useParams();
  const [tournament, setTournament] = useState(null);
  const [playerName, setPlayerName] = useState('');
  const [error, setError] = useState('');
  const [scoreUpdates, setScoreUpdates] = useState({}); // store scores for update

  // Fetch tournament
  const fetchTournament = useCallback(async () => {
    try {
      const res = await API.get(`/tournaments/${id}`);
      setTournament(res.data);
    } catch (err) {
      console.error('Error fetching tournament:', err);
      setError('Unable to load tournament data.');
    }
  }, [id]);

  useEffect(() => {
    fetchTournament();
  }, [fetchTournament]);

  // Add Player
  const addPlayer = async () => {
    if (!playerName.trim()) return;
    try {
      await API.post(`/tournaments/${id}/players`, { name: playerName });
      setPlayerName('');
      fetchTournament();
    } catch (err) {
      console.error('Error adding player:', err);
      setError('Failed to add player.');
    }
  };

  // Delete Player
  const deletePlayer = async (playerId) => {
    try {
      await API.delete(`/tournaments/${id}/players/${playerId}`);
      fetchTournament();
    } catch (err) {
      console.error('Error deleting player:', err);
      setError('Failed to delete player.');
    }
  };

  // Edit Player Name
  const editPlayerName = async (playerId, currentName) => {
    const newName = prompt('Enter new name:', currentName);
    if (!newName || newName === currentName) return;
    try {
      await API.put(`/tournaments/${id}/players/${playerId}`, { name: newName });
      fetchTournament();
    } catch (err) {
      console.error('Error updating player:', err);
      setError('Failed to update player.');
    }
  };

  // Generate Round
  const generateRound = async () => {
    try {
      await API.post(`/swiss/${id}/next-round`);
      fetchTournament();
    } catch (err) {
      console.error('Error generating round:', err);
      setError('Could not generate round.');
    }
  };

  // Handle score input
  const handleScoreChange = (pairIndex, playerKey, value) => {
    setScoreUpdates((prev) => ({
      ...prev,
      [pairIndex]: {
        ...prev[pairIndex],
        [playerKey]: parseFloat(value),
      },
    }));
  };

  // Submit scores (basic example)
  const submitScores = async (roundNumber) => {
    try {
      const payload = Object.entries(scoreUpdates).map(([index, result]) => ({
        roundNumber,
        pairIndex: parseInt(index),
        p1Score: result.p1,
        p2Score: result.p2,
      }));
      await API.post(`/swiss/${id}/submit-scores`, payload);
      setScoreUpdates({});
      fetchTournament();
    } catch (err) {
      console.error('Error submitting scores:', err);
      setError('Failed to submit scores.');
    }
  };

  if (!tournament) return <div className="container mt-4">Loading tournament...</div>;

  // Sort players by score
  const sortedPlayers = [...tournament.players].sort((a, b) => b.score - a.score);

  return (
    <div className="container mt-4">
      <h2>{tournament.name}</h2>
      {error && <div className="alert alert-danger">{error}</div>}

      {/* Add Player */}
      <div className="mb-3">
        <input
          type="text"
          placeholder="Player Name"
          value={playerName}
          onChange={(e) => setPlayerName(e.target.value)}
          className="form-control"
        />
        <button className="btn btn-success mt-2" onClick={addPlayer}>
          ‚ûï Add Player
        </button>
        <button className="btn btn-warning mt-2 ms-2" onClick={generateRound}>
          ‚ôüÔ∏è Generate Round
        </button>
      </div>

      {/* Players List */}
      <h4>Players (Sorted by Score)</h4>
      <ul className="list-group">
        {sortedPlayers.map((p) => (
          <li className="list-group-item d-flex justify-content-between align-items-center" key={p._id}>
            <div>
              <strong>{p.name}</strong> - {p.score} pts
            </div>
            <div>
              <button className="btn btn-sm btn-outline-primary me-2" onClick={() => editPlayerName(p._id, p.name)}>
                ‚úèÔ∏è Edit
              </button>
              <button className="btn btn-sm btn-outline-danger" onClick={() => deletePlayer(p._id)}>
                üóëÔ∏è Delete
              </button>
            </div>
          </li>
        ))}
      </ul>

      {/* Rounds */}
      <h4 className="mt-4">Rounds</h4>
      {tournament.rounds?.map((round, roundIndex) => (
        <div className="card mt-2" key={round._id || roundIndex}>
          <div className="card-body">
            <h5 className="card-title">Round {round.number}</h5>
            <ul className="list-group">
              {round.pairings?.map((pair, idx) => (
                <li className="list-group-item d-flex align-items-center justify-content-between" key={idx}>
                  <div>
                    {pair.p1?.name || 'BYE'} vs {pair.p2?.name || 'BYE'}
                  </div>
                  {pair.p1 && pair.p2 && (
                    <div className="d-flex align-items-center">
                      <input
                        type="number"
                        min="0"
                        max="1"
                        step="0.5"
                        placeholder="P1"
                        className="form-control form-control-sm me-2"
                        style={{ width: '60px' }}
                        onChange={(e) => handleScoreChange(idx, 'p1', e.target.value)}
                      />
                      <input
                        type="number"
                        min="0"
                        max="1"
                        step="0.5"
                        placeholder="P2"
                        className="form-control form-control-sm"
                        style={{ width: '60px' }}
                        onChange={(e) => handleScoreChange(idx, 'p2', e.target.value)}
                      />
                    </div>
                  )}
                </li>
              ))}
            </ul>
            {round.pairings?.length > 0 && (
              <button className="btn btn-primary mt-2" onClick={() => submitScores(round.number)}>
                ‚úÖ Submit Scores
              </button>
            )}
          </div>
        </div>
      ))}
    </div>
  );
};

export default ViewTournament;
