import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import API from '../utils/api';

const ViewTournament = () => {
const { id } = useParams();
const [tournament, setTournament] = useState(null);
const [playerName, setPlayerName] = useState('');

const fetchTournament = useCallback(async () => {
const res = await API.get(`/tournaments/${id}`);
setTournament(res.data);
}, [id]);

useEffect(() => {
  fetchTournament();
}, [fetchTournament]);

  const addPlayer = async () => {
    if (!playerName.trim()) return;
    await API.post(`/tournaments/${id}/players`, { name: playerName });
    setPlayerName('');
    fetchTournament();
  };

  const generateRound = async () => {
    await API.post(`/swiss/${id}/next-round`);
    fetchTournament();
  };

  if (!tournament) return <div className="container mt-4">Loading...</div>;

  return (
    <div className="container mt-4">
      <h2>{tournament.name}</h2>
      <div className="mb-4">
        <div className="input-group">
          <input
            type="text"
            className="form-control"
            placeholder="Player Name"
            value={playerName}
            onChange={(e) => setPlayerName(e.target.value)}
          />
          <button className="btn btn-success" onClick={addPlayer}>
            Add Player
          </button>
          <button className="btn btn-warning ms-2" onClick={generateRound}>
            Generate Round
          </button>
        </div>
      </div>

      <h4>Players:</h4>
      <ul className="list-group mb-4">
        {tournament.players.map((p) => (
          <li className="list-group-item d-flex justify-content-between" key={p._id}>
            <span>{p.name}</span>
            <span>{p.score} pts</span>
          </li>
        ))}
      </ul>

      <h4>Rounds:</h4>
      {tournament.rounds.map((r) => (
        <div className="card mb-3" key={r._id}>
          <div className="card-body">
            <h5 className="card-title">Round {r.number}</h5>
            <ul className="list-group">
              {r.pairings.map((pair, idx) => (
                <li className="list-group-item" key={idx}>
                  {pair.p1?.name || 'BYE'} vs {pair.p2?.name || 'BYE'}
                </li>
              ))}
            </ul>
          </div>
        </div>
      ))}
    </div>
  );
};

export default ViewTournament;
