import React, { useEffect, useState, useCallback } from 'react';
import { useParams } from 'react-router-dom';
import API from '../utils/api';

const ViewTournament = () => {
  const { id } = useParams();
  const [tournament, setTournament] = useState(null);
  const [playerName, setPlayerName] = useState('');
  const [error, setError] = useState('');
  const [editingScores, setEditingScores] = useState({});
  const [editingNameId, setEditingNameId] = useState(null);
  const [editingName, setEditingName] = useState('');

  const fetchTournament = useCallback(async () => {
    try {
      const res = await API.get(`/tournaments/${id}`);
      setTournament(res.data);
    } catch (err) {
      console.error('Error fetching tournament:', err);
      setError('Unable to load tournament data.');
    }
  }, [id]);

  useEffect(() => {
    fetchTournament();
  }, [fetchTournament]);

  const addPlayer = async () => {
    try {
      await API.post(`/tournaments/${id}/players`, { name: playerName });
      setPlayerName('');
      fetchTournament();
    } catch (err) {
      console.error('Error adding player:', err);
      setError('Failed to add player.');
    }
  };

  const deletePlayer = async (playerId) => {
    try {
      await API.delete(`/tournaments/${id}/players/${playerId}`);
      fetchTournament();
    } catch (err) {
      console.error('Error deleting player:', err);
      setError('Failed to delete player.');
    }
  };

  const updatePlayerName = async () => {
    try {
      await API.put(`/tournaments/${id}/players/${editingNameId}`, { name: editingName });
      setEditingName('');
      setEditingNameId(null);
      fetchTournament();
    } catch (err) {
      console.error('Error updating player name:', err);
      setError('Failed to update player name.');
    }
  };

  const generateRound = async () => {
    try {
      await API.post(`/swiss/${id}/next-round`);
      fetchTournament();
    } catch (err) {
      console.error('Error generating round:', err);
      setError('Could not generate round.');
    }
  };

  const handleScoreChange = (pairingIndex, playerKey, value) => {
    setEditingScores((prev) => ({
      ...prev,
      [`${pairingIndex}-${playerKey}`]: value,
    }));
  };

  const submitScores = async (roundIndex) => {
    const updates = tournament.rounds[roundIndex].pairings.map((pair, idx) => {
      const p1Score = parseFloat(editingScores[`${idx}-p1`] || 0);
      const p2Score = pair.p2 ? parseFloat(editingScores[`${idx}-p2`] || 0) : 0;

      return [
        { id: pair.p1._id, score: p1Score },
        pair.p2 ? { id: pair.p2._id, score: p2Score } : null,
      ].filter(Boolean);
    });

    try {
      await API.put(`/tournaments/${id}/update-scores`, { updates: updates.flat() });
      setEditingScores({});
      fetchTournament();
    } catch (err) {
      console.error('Failed to submit scores:', err);
      setError('Score update failed.');
    }
  };

  if (!tournament) return <div className="container mt-4">Loading tournament...</div>;

  const sortedPlayers = [...tournament.players].sort((a, b) => b.score - a.score);

  return (
    <div className="container mt-4">
      <h2>{tournament.name}</h2>

      {error && <div className="alert alert-danger">{error}</div>}

      <div className="mb-3">
        <input
          type="text"
          placeholder="Player Name"
          value={playerName}
          onChange={(e) => setPlayerName(e.target.value)}
          className="form-control"
        />
        <button className="btn btn-success mt-2" onClick={addPlayer}>Add Player</button>
        <button className="btn btn-warning mt-2 ms-2" onClick={generateRound}>Generate Round</button>
      </div>

      <h4>Players (Leaderboard):</h4>
      <ul className="list-group mb-4">
        {sortedPlayers.map((p) => (
          <li className="list-group-item d-flex justify-content-between align-items-center" key={p._id}>
            {editingNameId === p._id ? (
              <>
                <input
                  type="text"
                  className="form-control me-2"
                  value={editingName}
                  onChange={(e) => setEditingName(e.target.value)}
                />
                <button className="btn btn-primary btn-sm me-1" onClick={updatePlayerName}>Save</button>
                <button className="btn btn-secondary btn-sm" onClick={() => setEditingNameId(null)}>Cancel</button>
              </>
            ) : (
              <>
                {p.name} — {p.score} pts
                <span>
                  <button className="btn btn-sm btn-outline-primary me-2" onClick={() => {
                    setEditingNameId(p._id);
                    setEditingName(p.name);
                  }}>Edit</button>
                  <button className="btn btn-sm btn-outline-danger" onClick={() => deletePlayer(p._id)}>Delete</button>
                </span>
              </>
            )}
          </li>
        ))}
      </ul>

      <h4>Rounds:</h4>
      {tournament.rounds.map((round, roundIndex) => (
        <div className="card mb-3" key={round._id || roundIndex}>
          <div className="card-body">
            <h5 className="card-title">Round {round.number}</h5>
            {round.pairings.map((pair, idx) => (
              <div key={idx} className="d-flex align-items-center mb-2">
                <div className="me-2" style={{ width: '30%' }}>
                  {pair.p1?.name || 'BYE'}
                  <input
                    type="number"
                    step="0.5"
                    min="0"
                    className="form-control"
                    value={editingScores[`${idx}-p1`] || ''}
                    onChange={(e) => handleScoreChange(idx, 'p1', e.target.value)}
                  />
                </div>
                <span className="mx-2">vs</span>
                <div className="me-2" style={{ width: '30%' }}>
                  {pair.p2?.name || 'BYE'}
                  {pair.p2 && (
                    <input
                      type="number"
                      step="0.5"
                      min="0"
                      className="form-control"
                      value={editingScores[`${idx}-p2`] || ''}
                      onChange={(e) => handleScoreChange(idx, 'p2', e.target.value)}
                    />
                  )}
                </div>
              </div>
            ))}
            <button className="btn btn-primary mt-2" onClick={() => submitScores(roundIndex)}>
              Submit Scores
            </button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default ViewTournament;
