import React, { useEffect, useState, useCallback } from 'react';
import { useParams } from 'react-router-dom';
import API from '../utils/api';

const ViewTournament = () => {
  const { id } = useParams();
  const [tournament, setTournament] = useState(null);
  const [playerName, setPlayerName] = useState('');
  const [error, setError] = useState('');
  const [scoreChanges, setScoreChanges] = useState({});

  const fetchTournament = useCallback(async () => {
    try {
      const res = await API.get(`/tournaments/${id}`);
      setTournament(res.data);
    } catch (err) {
      console.error('Error fetching tournament:', err);
      setError('Unable to load tournament data.');
    }
  }, [id]);

  useEffect(() => {
    fetchTournament();
  }, [fetchTournament]);

  const addPlayer = async () => {
    if (!playerName.trim()) return;
    try {
      await API.post(`/tournaments/${id}/players`, { name: playerName });
      setPlayerName('');
      fetchTournament();
    } catch (err) {
      console.error('Error adding player:', err);
      setError('Failed to add player.');
    }
  };

  const deletePlayer = async (playerId) => {
    try {
      await API.delete(`/tournaments/${id}/players/${playerId}`);
      fetchTournament();
    } catch (err) {
      console.error('Error deleting player:', err);
      setError('Failed to delete player.');
    }
  };

  const editPlayer = async (playerId, currentName, currentScore) => {
    const newName = prompt('New name:', currentName) || currentName;
    const newScore = parseFloat(prompt('New score (0, 0.5, 1):', currentScore));
    if (isNaN(newScore)) {
      alert('Invalid score');
      return;
    }

    try {
      await API.put(`/tournaments/${id}/players/${playerId}`, {
        name: newName,
        score: newScore,
      });
      fetchTournament();
    } catch (err) {
      console.error('Error editing player:', err);
      setError('Failed to update player.');
    }
  };

  const generateRound = async () => {
    try {
      await API.post(`/swiss/${id}/next-round`);
      fetchTournament();
    } catch (err) {
      console.error('Error generating round:', err);
      setError('Could not generate round.');
    }
  };

  const handleScoreChange = (roundNumber, playerId, value) => {
    setScoreChanges((prev) => ({
      ...prev,
      [roundNumber]: {
        ...prev[roundNumber],
        [playerId]: parseFloat(value),
      },
    }));
  };

  const submitRoundScores = async (roundNumber) => {
    const scores = scoreChanges[roundNumber] || {};
    const payload = Object.entries(scores).map(([playerId, score]) => ({
      playerId,
      score,
    }));

    try {
      await API.post(`/swiss/${id}/submit-scores`, {
        roundNumber,
        scores: payload,
      });
      setScoreChanges((prev) => {
        const updated = { ...prev };
        delete updated[roundNumber];
        return updated;
      });
      fetchTournament();
    } catch (err) {
      console.error('Error submitting scores:', err);
      setError('Failed to submit scores.');
    }
  };

  if (!tournament) return <div className="container mt-4">Loading tournament...</div>;

  const sortedPlayers = [...tournament.players].sort((a, b) => b.score - a.score);

  return (
    <div className="container mt-4">
      <h2>{tournament.name}</h2>

      {error && <div className="alert alert-danger">{error}</div>}

      <div className="mb-3">
        <input
          type="text"
          placeholder="Player Name"
          value={playerName}
          onChange={(e) => setPlayerName(e.target.value)}
          className="form-control"
        />
        <button className="btn btn-success mt-2" onClick={addPlayer}>
          ‚ûï Add Player
        </button>
        <button className="btn btn-warning mt-2 ms-2" onClick={generateRound}>
          ‚ôüÔ∏è Generate Round
        </button>
      </div>

      <h4>Players (Sorted by Score)</h4>
      <ul className="list-group">
        {sortedPlayers.map((p) => (
          <li className="list-group-item d-flex justify-content-between align-items-center" key={p._id}>
            <div>
              <strong>{p.name}</strong> - {p.score} pts
            </div>
            <div>
              <button
                className="btn btn-sm btn-outline-primary me-2"
                onClick={() => editPlayer(p._id, p.name, p.score)}
              >
                ‚úèÔ∏è Edit
              </button>
              <button className="btn btn-sm btn-outline-danger" onClick={() => deletePlayer(p._id)}>
                üóëÔ∏è Delete
              </button>
            </div>
          </li>
        ))}
      </ul>

      <h4 className="mt-4">Rounds</h4>
      {tournament.rounds?.map((round, i) => (
        <div className="card mt-3" key={round._id || i}>
          <div className="card-body">
            <h5 className="card-title">Round {round.number}</h5>
            <ul className="list-group">
              {sortedPlayers.map((player) => (
                <li className="list-group-item d-flex justify-content-between align-items-center" key={player._id}>
                  <span>{player.name}</span>
                  <input
                    type="number"
                    min="0"
                    max="1"
                    step="0.5"
                    className="form-control form-control-sm"
                    style={{ width: '70px' }}
                    placeholder={`${player.score}`}
                    value={
                      (scoreChanges[round.number]?.[player._id] ?? '')
                    }
                    onChange={(e) =>
                      handleScoreChange(round.number, player._id, e.target.value)
                    }
                  />
                </li>
              ))}
            </ul>
            <button
              className="btn btn-primary mt-3"
              onClick={() => submitRoundScores(round.number)}
            >
              ‚úÖ Submit Scores
            </button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default ViewTournament;
