import React, { useEffect, useState, useCallback } from 'react';
import { useParams } from 'react-router-dom';
import API from '../utils/api';

const ViewTournament = () => {
  const { id } = useParams();
  const [tournament, setTournament] = useState(null);
  const [playerName, setPlayerName] = useState('');
  const [editingPlayer, setEditingPlayer] = useState(null);
  const [scoreChanges, setScoreChanges] = useState({});
  const [error, setError] = useState('');

  // Fetch tournament data
  const fetchTournament = useCallback(async () => {
    try {
      const res = await API.get(`/tournaments/${id}`);
      setTournament(res.data);
    } catch (err) {
      console.error('Error fetching tournament:', err);
      setError('Unable to load tournament data.');
    }
  }, [id]);

  useEffect(() => {
    fetchTournament();
  }, [fetchTournament]);

  // Add or update player
  const handlePlayerSubmit = async () => {
    try {
      if (editingPlayer) {
        await API.put(`/tournaments/${id}/players/${editingPlayer._id}`, { name: playerName });
      } else {
        await API.post(`/tournaments/${id}/players`, { name: playerName });
      }
      setPlayerName('');
      setEditingPlayer(null);
      fetchTournament();
    } catch (err) {
      console.error('Error saving player:', err);
      setError('Failed to save player.');
    }
  };

  const editPlayer = (playerId, name) => {
    setEditingPlayer({ _id: playerId });
    setPlayerName(name);
  };

  const deletePlayer = async (playerId) => {
    try {
      await API.delete(`/tournaments/${id}/players/${playerId}`);
      fetchTournament();
    } catch (err) {
      console.error('Error deleting player:', err);
      setError('Failed to delete player.');
    }
  };

  const generateRound = async () => {
    try {
      await API.post(`/swiss/${id}/next-round`);
      fetchTournament();
    } catch (err) {
      console.error('Error generating round:', err);
      setError('Could not generate round.');
    }
  };

  const handleScoreChange = (roundNumber, playerId, value) => {
    setScoreChanges((prev) => ({
      ...prev,
      [roundNumber]: {
        ...prev[roundNumber],
        [playerId]: parseFloat(value),
      },
    }));
  };

  const submitRoundScores = async (roundNumber) => {
    const scores = scoreChanges[roundNumber] || {};
    const payload = Object.entries(scores).map(([playerId, score]) => ({
      playerId,
      score,
    }));

    try {
      await API.post(`/swiss/${id}/submit-scores`, {
        roundNumber,
        scores: payload,
      });
      setScoreChanges((prev) => {
        const updated = { ...prev };
        delete updated[roundNumber];
        return updated;
      });
      fetchTournament();
    } catch (err) {
      console.error('Error submitting scores:', err);
      setError('Failed to submit scores.');
    }
  };

  if (!tournament) return <div className="container mt-4">Loading tournament...</div>;

  const sortedPlayers = [...(tournament.players || [])].sort((a, b) => b.score - a.score);

  return (
    <div className="container mt-4">
      <h2>{tournament.name}</h2>

      {error && <div className="alert alert-danger">{error}</div>}

      <div className="mb-3">
        <input
          type="text"
          placeholder="Player Name"
          value={playerName}
          onChange={(e) => setPlayerName(e.target.value)}
          className="form-control"
        />
        <button className="btn btn-success mt-2" onClick={handlePlayerSubmit}>
          {editingPlayer ? 'Update Player' : 'Add Player'}
        </button>
        <button className="btn btn-warning mt-2 ms-2" onClick={generateRound}>
          Generate Round
        </button>
      </div>

      <h4>Players (Sorted by Score)</h4>
      <ul className="list-group">
        {sortedPlayers.map((p) => (
          <li className="list-group-item d-flex justify-content-between align-items-center" key={p._id}>
            <div>
              <strong>{p.name}</strong> - {p.score} pts
            </div>
            <div>
              <button className="btn btn-sm btn-outline-primary me-2" onClick={() => editPlayer(p._id, p.name)}>
                ✏️ Edit
              </button>
              <button className="btn btn-sm btn-outline-danger" onClick={() => deletePlayer(p._id)}>
                🗑️ Delete
              </button>
            </div>
          </li>
        ))}
      </ul>

      <h4 className="mt-4">Rounds</h4>
      {tournament.rounds?.map((round, i) => (
        <div key={round._id || i} className="card mt-3">
          <div className="card-body">
            <h5 className="card-title">Round {round.number}</h5>
            <ul className="list-group">
              {round.pairings?.map((pair, idx) => (
                <li className="list-group-item d-flex justify-content-between align-items-center" key={idx}>
                  <span>
                    {pair.p1?.name || 'BYE'} vs {pair.p2?.name || 'BYE'}
                  </span>
                  <span>
                    {pair.p1 && (
                      <select
                        value={scoreChanges[round.number]?.[pair.p1._id] ?? pair.p1.score ?? ''}
                        onChange={(e) => handleScoreChange(round.number, pair.p1._id, e.target.value)}
                        className="form-select form-select-sm me-2"
                      >
                        <option value="">--</option>
                        <option value="0">0</option>
                        <option value="0.5">0.5</option>
                        <option value="1">1</option>
                      </select>
                    )}
                    {pair.p2 && (
                      <select
                        value={scoreChanges[round.number]?.[pair.p2._id] ?? pair.p2.score ?? ''}
                        onChange={(e) => handleScoreChange(round.number, pair.p2._id, e.target.value)}
                        className="form-select form-select-sm"
                      >
                        <option value="">--</option>
                        <option value="0">0</option>
                        <option value="0.5">0.5</option>
                        <option value="1">1</option>
                      </select>
                    )}
                  </span>
                </li>
              ))}
            </ul>
            <button
              className="btn btn-sm btn-primary mt-2"
              onClick={() => submitRoundScores(round.number)}
            >
              Submit Scores for Round {round.number}
            </button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default ViewTournament;
