import React, { useEffect, useState, useCallback } from 'react';
import { useParams } from 'react-router-dom';
import API from '../utils/api';

const ViewTournament = () => {
  const { id } = useParams();
  const [tournament, setTournament] = useState(null);
  const [playerName, setPlayerName] = useState('');
  const [error, setError] = useState('');

  // Fetch tournament data
  const fetchTournament = useCallback(async () => {
    try {
      const res = await API.get(`/tournaments/${id}`);
      setTournament(res.data);
    } catch (err) {
      console.error('Error fetching tournament:', err);
      setError('Unable to load tournament data.');
    }
  }, [id]);

  useEffect(() => {
    fetchTournament();
  }, [fetchTournament]);

  // Add player
  const addPlayer = async () => {
    try {
      await API.post(`/tournaments/${id}/players`, { name: playerName });
      setPlayerName('');
      fetchTournament();
    } catch (err) {
      console.error('Error adding player:', err);
      setError('Failed to add player.');
    }
  };

  // Update player
  const updatePlayer = async (playerId, name, score) => {
    try {
      await API.patch(`/tournaments/${id}/players/${playerId}`, { name, score });
      fetchTournament();
    } catch (err) {
      console.error('Failed to update player:', err);
      setError('Error updating player.');
    }
  };

  // Delete player
  const deletePlayer = async (playerId) => {
    try {
      await API.delete(`/tournaments/${id}/players/${playerId}`);
      fetchTournament();
    } catch (err) {
      console.error('Failed to delete player:', err);
      setError('Error deleting player.');
    }
  };

  // Generate next round
  const generateRound = async () => {
    try {
      await API.post(`/swiss/${id}/next-round`);
      fetchTournament();
    } catch (err) {
      console.error('Error generating round:', err);
      setError('Could not generate round.');
    }
  };

  if (!tournament) {
    return <div className="container mt-4">Loading tournament...</div>;
  }

  return (
    <div className="container mt-4">
      <h2>{tournament.name}</h2>

      {error && <div className="alert alert-danger">{error}</div>}

      <div className="mb-3">
        <input
          type="text"
          placeholder="Player Name"
          value={playerName}
          onChange={(e) => setPlayerName(e.target.value)}
          className="form-control"
        />
        <button className="btn btn-success mt-2" onClick={addPlayer}>
          Add Player
        </button>
        <button className="btn btn-warning mt-2 ms-2" onClick={generateRound}>
          Generate Round
        </button>
      </div>

      <h4>Players:</h4>
      <ul className="list-group">
        {tournament.players?.map((p, index) => (
          <li className="list-group-item d-flex justify-content-between align-items-center" key={p._id || index}>
            <div className="d-flex gap-2">
              <input
                type="text"
                value={p.name}
                onChange={(e) => {
                  const updated = [...tournament.players];
                  updated[index].name = e.target.value;
                  setTournament({ ...tournament, players: updated });
                }}
                className="form-control"
              />
              <input
                type="number"
                value={p.score}
                onChange={(e) => {
                  const updated = [...tournament.players];
                  updated[index].score = parseInt(e.target.value) || 0;
                  setTournament({ ...tournament, players: updated });
                }}
                className="form-control"
              />
            </div>
            <div>
              <button
                className="btn btn-primary btn-sm me-2"
                onClick={() => updatePlayer(p._id, p.name, p.score)}
              >
                Update
              </button>
              <button
                className="btn btn-danger btn-sm"
                onClick={() => deletePlayer(p._id)}
              >
                Delete
              </button>
            </div>
          </li>
        ))}
      </ul>

      <h4 className="mt-4">Rounds:</h4>
      {tournament.rounds?.length > 0 ? (
        tournament.rounds.map((r, i) => (
          <div key={r._id || i} className="card mt-2">
            <div className="card-body">
              <h5 className="card-title">Round {r.number}</h5>
              <ul className="list-group">
                {r.pairings?.map((pair, idx) => (
                  <li className="list-group-item" key={idx}>
                    {pair.p1?.name || 'BYE'} vs {pair.p2?.name || 'BYE'}
                  </li>
                ))}
              </ul>
            </div>
          </div>
        ))
      ) : (
        <p>No rounds generated yet.</p>
      )}
    </div>
  );
};

export default ViewTournament;
